import React, { useContext } from "react";
import { BackgroundColorContext } from "contexts/BackgroundColorContext";
import FixedPlugin from "components/FixedPlugin/FixedPlugin.js";
import { Button } from "reactstrap";

function BeforeLogin() {
    const { color, changeColor } = useContext(BackgroundColorContext);

    // white-content 클래스가 있는지 체크
    const isWhiteContent = document.body.classList.contains("white-content");

    const handleKakaoLogin = () => {
        // 환경 변수에서 값을 가져오기
        const clientId = process.env.REACT_APP_CLIENT_ID;
        const redirectUri = process.env.REACT_APP_REDIRECT_URI;
        
        // GET 요청을 보낼 엔드포인트 URL
        const apiUrl = `https://kauth.kakao.com/oauth/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=code`;
        window.location.href = apiUrl;
    }

    return (
        <React.Fragment>
            <div className="wrapper">
                <Button
                    style={{
                        backgroundImage: 'none',
                        color: isWhiteContent ? '#000000' : '#ffffff', // white-content 클래스가 있으면 검정색, 없으면 흰색
                        fontSize: '18px',
                        padding: '10px 20px',
                        borderRadius: '25px',
                    }}
                    className={`bg-${isWhiteContent ? 'dark' : 'white'}`} // white-content 클래스가 있으면 dark, 없으면 white
                    onClick={handleKakaoLogin}
                >
                    Login with Kakao
                </Button>
            </div>
            <FixedPlugin bgColor={color} handleBgClick={changeColor} />
        </React.Fragment>
    );
}

export default BeforeLogin;
