import React, { useState } from "react";
import { BackgroundColorContext } from "contexts/BackgroundColorContext";
import FixedPlugin from "components/FixedPlugin/FixedPlugin.js";
import { Button } from "reactstrap";

function BeforeLogin() {
    const [isWhiteContent, setIsWhiteContent] = useState(false); // 초기값은 false로 설정

    const handleKakaoLogin = () => {
        // 환경 변수에서 값을 가져오기
        const clientId = process.env.REACT_APP_CLIENT_ID;
        const redirectUri = process.env.REACT_APP_REDIRECT_URI;
        
        // GET 요청을 보낼 엔드포인트 URL
        const apiUrl = `https://kauth.kakao.com/oauth/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=code`;
        window.location.href = apiUrl;
        console.log(clientId, redirectUri);
    };

    return (
        <BackgroundColorContext.Consumer>
            {({ color, changeColor }) => (
                <React.Fragment>
                    <div
                        className={`wrapper${isWhiteContent ? " white-content" : ""}`} // 클래스 이름을 동적으로 설정
                        style={{
                            display: "flex",
                            justifyContent: "center",
                            alignItems: "center",
                            height: "100vh",
                        }}
                    >
                        <Button
                            style={{
                                backgroundImage: "none",
                                color: isWhiteContent ? "#000000" : "#ffffff", // 조건에 따라 색상 변경
                                fontSize: "24px",
                                padding: "20px 40px",
                                borderRadius: "50px",
                            }}
                            className={isWhiteContent ? "bg-white" : "bg-dark"} // 조건에 따라 클래스 변경
                            onClick={handleKakaoLogin}
                        >
                            Login with Kakao
                        </Button>
                    </div>
                    <FixedPlugin bgColor={color} handleBgClick={changeColor} />
                </React.Fragment>
            )}
        </BackgroundColorContext.Consumer>
    );
}

export default BeforeLogin;
