import React from "react";
import { BackgroundColorContext } from "contexts/BackgroundColorContext";
import FixedPlugin from "components/FixedPlugin/FixedPlugin.js";
import { Button } from "reactstrap";

function BeforeLogin() {
    const handleKakaoLogin = () => {
        // 환경 변수에서 값을 가져오기
        const clientId = process.env.REACT_APP_CLIENT_ID;
        const redirectUri = process.env.REACT_APP_REDIRECT_URI;
        
        // GET 요청을 보낼 엔드포인트 URL
        const apiUrl = `https://kauth.kakao.com/oauth/authorize?client_id=${clientId}&redirect_uri=${redirectUri}&response_type=code`; // GET 요청을 보낼 URL로 변경
        // console.log('확인')
        window.location.href = apiUrl
        console.log(clientId, redirectUri)
      }
    return (
        <BackgroundColorContext.Consumer>
            {({ color, changeColor }) => (
                <React.Fragment>
                <div className="wrapper"
                    style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        height: "100vh", // 화면 높이를 창 높이로 설정
                    }}
                >
                    {/* <Sidebar
                    routes={routes}
                    logo={{
                        outterLink: "https://www.creative-tim.com/",
                        text: "Creative Tim",
                        imgSrc: logo,
                    }}
                    toggleSidebar={toggleSidebar}
                    /> */}
                    {/* <div className="main-panel" ref={mainPanelRef} data={color}>
                    <AdminNavbar
                        brandText={getBrandText(location.pathname)}
                        toggleSidebar={toggleSidebar}
                        sidebarOpened={sidebarOpened}
                    />
                    <Routes>
                        {getRoutes(routes)}
                        <Route
                        path="/"
                        element={<Navigate to="/admin/dashboard" replace />}
                        />
                    </Routes>
                    {
                        // we don't want the Footer to be rendered on map page
                        location.pathname === "/admin/maps" ? null : <Footer fluid />
                    }
                    </div> */}
                    <Button
                    style={{
                        backgroundImage: "none",
                        color: "#000000",
                        fontSize: "24px", // 버튼의 글자 크기 조절
                        padding: "20px 40px", // 버튼의 내용 안의 여백 조절
                        borderRadius: "50px", // 버튼의 테두리를 더 둥글게 조절
                    }}
                    className="bg-white"
                    onClick={handleKakaoLogin}
                    >
                        Login with Kakao
                    </Button>
                    
                </div>
                <FixedPlugin bgColor={color} handleBgClick={changeColor} />
                </React.Fragment>
            )}
        </BackgroundColorContext.Consumer>
    )
}

export default BeforeLogin
